import React, { Component } from 'react';

import Container from 'react-bootstrap/Container'
import Jumbotron from 'react-bootstrap/Jumbotron'
import Button from 'react-bootstrap/Button'

import Background from '../assets/headerBackground.jpg'

const styles = {
	jumbotron: {
		height: '100vh',
		width: '100vw',
		backgroundImage: `url(${Background})`,
		margin: '0',

		/* Center and scale the image nicely */
	    backgroundPosition: 'center',
	    backgroundRepeat: 'no-repeat',
	    backgroundSize: 'cover'
	}
}

class Header extends Component {
  render() {

    return (
		<section id="header">
			<Jumbotron fluid style = {styles.jumbotron}>
				<Container fluid>
				</Container>
				<h1>Jacob Makarsky</h1>
				<p>
					State College based Software Engineer & Entrepreneur. 
					Co-Founder of LiveBet, Partner at Digital Wealth Management.
				</p>
				<p>
					<Button variant="primary">Learn more</Button>
				</p>
			</Jumbotron>
		</section>
    );
  }
}

export default Header;
